import React from "react";
import styled from "styled-components";

const Image = (props) => {
    const {shape, src, size} = props;

    const styles = {
        src: src,
        size: size,
    }

    if(shape === "circle"){
        return (
            <ImageCircle {...styles}></ImageCircle>
        )
    }

    return (
    <React.Fragment>
    </React.Fragment>
    )
}

Image.defaultProps = {
    shape: "circle",
    src: "https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.ikea.com%2Fkr%2Fen%2Fp%2Fdjungelskog-soft-toy-lion-60402834%2F&psig=AOvVaw1vgNTd2cM5sJAdiLKgqm7w&ust=1633088326872000&source=images&cd=vfe&ved=0CAsQjRxqFwoTCLiBsaDOpvMCFQAAAAAdAAAAABAD",
    size: 36,
};

const ImageCircle = styled.div`
    /* --size: ${(props) => props.size}px; */
    width: var(--size);
    height: var(--size);
    border-radius: var(--size);

    background-image: url("${(props) => props.src}");
    background-size: cover;
    margin: 4px;
`;

export default Image;
