import React from "react";
import Grid from "../elements/Grid";
import Image from "../elements/Image";
import Text from "../elements/Text";

const Post = (props) => {
    return (
        <React.Fragment>
            <Grid>
                <Grid iis_flexs_flex>
                    <Image shape="circle" src={props.src} />
                    <Text bold>{props.user_info.user_name}</Text>
                </Grid>

                <Grid padding="16px"></Grid>
                <Grid>
                    <Image shape="rectangle" src={props.src} />
                </Grid>
                <Grid padding="16px"></Grid>

                <div>user profile / user name / insert_dt / is_me (edit btn)</div>
                <div>contents</div>
                <div>image</div>
                <div>comment cnt</div>
            </Grid>
        </React.Fragment>
    )
}

Post.defaultProps = {
    user_info: {
        user_name: "Kyoo",
        user_profile: "../IMG_0985.JPG",
    },
    image_url: "https://www.ikea.com/kr/en/images/products/djungelskog-soft-toy-lion__0877742_pe662341_s5.jpg?f=s",
    contents: "고양이네요!",
    comment_cnt: 10,
    insert_dt: "2021-09-30 17:32:00",
};


export default Post
